mapper.insert:
INSERT INTO 
	edt_policy
(
	policy
	, service
	, write_time
	, company_id
)
VALUES
(
	#{policy}
	, #{service}
	, CURRENT_TIME
	, #{company_id}
)

mapper.update:
UPDATE
	edt_policy
SET
	policy = #{policy}
	, service = #{service}
	, write_time = CURRENT_TIME
WHERE
	company_id = #{company_id}
